import React, { useState } from 'react';
import './MainPage.css';
import storeData from './data/storeData'; // Adjust the path as necessary

function MainPage() {
  const [selectedStore, setSelectedStore] = useState('');
  const [searchQuery, setSearchQuery] = useState('');

  const handleSelectStore = (store) => {
    setSelectedStore(store.name);
    console.log('Selected store:', store.name);
  };

  const handleSearch = (event) => {
    setSearchQuery(event.target.value);
  };

  const filteredStores = storeData.filter(store =>
    store.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="main-page">
      <header className="header">
        <div className="logo-heading-container">
          <img src="/logo.png" alt="DineCampus Logo" className="logo" />
          <h1 className="heading">DineCampus</h1>
        </div>
        <div className="search-container">
          <input
            type="text"
            placeholder="Search for a store..."
            value={searchQuery}
            onChange={handleSearch}
            className="search-bar"
          />
        </div>
      </header>
      <div className="stores-container">
        {filteredStores.map((store) => (
          <div key={store.id} className="store-item" onClick={() => handleSelectStore(store)}>
            <img src={store.icon} alt={`Store ${store.id}`} className="store-icon" />
            <div className="store-name">{store.name}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MainPage;
