import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api/axios';
import './MainPage.css';

function MainPage({ user, setUser }) {
  const [searchQuery, setSearchQuery] = useState('');
  const [stores, setStores] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStores = async () => {
      try {
        const response = await api.get('/stores');
        setStores(response.data);
      } catch (error) {
        console.error('Error fetching stores:', error);
      }
    };

    fetchStores();
  }, []);

  const handleSelectStore = (store) => {
    navigate(`/store/${store.id}`);
  };

  const handleSearch = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    navigate('/');
  };

  const filteredStores = stores.filter((store) =>
    store.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="main-page">
      <header className="header">
        <div className="logo-heading-container">
          <img src="/images/logo.png" alt="DineCampus Logo" className="logo" />
          <h1 className="heading">好好吃饭！</h1>
        </div>
        <div className="search-container">
          <input
            type="text"
            placeholder="Search for a store..."
            value={searchQuery}
            onChange={handleSearch}
            className="search-bar"
          />
        </div>
        <div className="auth-buttons">
          {!user ? (
            <>
              <button className="auth-button" onClick={() => navigate('/login')}>登录</button>
              <button className="auth-button" onClick={() => navigate('/register')}>注册</button>
            </>
          ) : (
            <>
              {user.role === 'staff' && (
                <button className="auth-button" onClick={() => navigate('/staff-dashboard')}>Staff Dashboard</button>
              )}
              <button className="auth-button" onClick={handleLogout}>Logout</button>
            </>
          )}
        </div>
      </header>
      <div className="stores-container">
        {filteredStores.map((store) => (
          <div key={store.id} className="store-item" onClick={() => handleSelectStore(store)}>
            <img src={store.icon} alt={`Store ${store.id}`} className="store-icon" />
            <div className="store-name">{store.name}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MainPage;
