import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Cart.css';

const Cart = ({ cart, setCart }) => {
  const navigate = useNavigate();

  const handleRemoveItem = (itemId) => {
    const updatedCart = cart.filter(item => item.id !== itemId);
    setCart(updatedCart);
  };

  return (
    <div className="cart-page">
      <div className="cart-header">
        <h1>Cart</h1>
        <div className="cart-header-buttons">
          <button onClick={() => navigate('/preorder')} className="cart-preorder-button">Preorder</button>
          <button onClick={() => navigate('/checkout')} className="cart-checkout-button">Checkout</button>
        </div>
      </div>
      <div className="cart-container">
        {cart.length === 0 ? (
          <p>Your cart is empty</p>
        ) : (
          <div className="cart-items">
            {cart.map((item) => (
              <div key={item.id} className="cart-item">
                {item.image && (
                  <img src={`/images/${item.image}`} alt={item.name} className="cart-item-image" />
                )}
                <div className="cart-item-details">
                  <h3>{item.name}</h3>
                  <p>{item.price} å…ƒ</p>
                  <p>Quantity: {item.quantity}</p>
                  <button onClick={() => handleRemoveItem(item.id)} className="cart-remove-button">Remove</button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Cart;
