// src/components/Checkout.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import './Checkout.css';

const Checkout = () => {
  const { cartItems, clearCart } = useCart();
  const [ticketNumber, setTicketNumber] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('');
  const navigate = useNavigate();

  const totalAmount = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);
  const userId = 1; // Replace with actual user ID

  const handlePaymentMethodChange = (event) => {
    setPaymentMethod(event.target.value);
  };

  const handleCheckout = async () => {
    if (!paymentMethod) {
      alert('Please select a payment method.');
      return;
    }

    const paymentStatus = paymentMethod === 'online' ? 'PAID' : 'PENDING';

    const orderDetails = {
      userId,
      storeId: cartItems[0].storeId, // Assuming all items are from the same store
      storeName: cartItems[0].storeName, // Assuming all items are from the same store
      items: cartItems,
      totalAmount,
      paymentStatus,
    };

    try {
      const response = await axios.post('/api/checkout', orderDetails);
      if (response.status === 200) {
        setTicketNumber(response.data.ticketNumber);
        alert('Order placed successfully');
        clearCart();
        navigate('/confirmation');
      }
    } catch (error) {
      console.error('Error placing order:', error);
      alert('Failed to place order');
    }
  };

  return (
    <div className="checkout-container">
      <header className="checkout-header">
        <h1>结算</h1>
        <div className="total-amount">总金额: {totalAmount} 元</div>
      </header>
      <div className="checkout-items">
        {cartItems.map((item) => (
          <div key={item.id} className="checkout-item">
            {item.image && <img src={`/images/${item.image}`} alt={item.name} className="checkout-item-image" />}
            <div className="checkout-item-details">
              <h3>{item.name}</h3>
              <p>价格: {item.price} 元</p>
              <p>数量: {item.quantity}</p>
            </div>
          </div>
        ))}
      </div>
      <div className="payment-method">
        <label>
          Payment Method:
          <select value={paymentMethod} onChange={handlePaymentMethodChange}>
            <option value="">Select Payment Method</option>
            <option value="online">Online Payment</option>
            <option value="store">Pay at Store</option>
          </select>
        </label>
      </div>
      <div className="checkout-actions">
        <button onClick={handleCheckout} className="checkout-button">Place Order</button>
      </div>
      {ticketNumber && <div className="ticket-number">Your Ticket Number: {ticketNumber}</div>}
    </div>
  );
};

export default Checkout;
