import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import MainPage from './components/MainPage'; // Import the MainPage component
import StorePage from './components/StorePage'; // Import the StorePage component
import ItemPage from './components/ItemPage';
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';

function App() {
  const [showSplash, setShowSplash] = useState(true);
  const [user, setUser] = useState(null);

  // Handle the splash screen visibility
  useEffect(() => {
    const timer = setTimeout(() => {
      const splashScreen = document.querySelector('.Splash-screen');
      if (splashScreen) {
        splashScreen.style.animation = 'fadeOut 2s';
      }
      setTimeout(() => setShowSplash(false), 1000); // Waits for fade-out to finish
    }, 2000); // Display splash screen for 2 seconds, then start fade-out

    return () => clearTimeout(timer); // Cleanup the timer
  }, []);

  useEffect(() => {
    const loggedInUser = localStorage.getItem('user');
    if (loggedInUser) {
      setUser(JSON.parse(loggedInUser));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
  };

  return (
    <Router>
      <div className="App">
        {showSplash ? (
          <div className="Splash-screen">
            <img src={`${process.env.PUBLIC_URL}/logo.png`} alt="logo" className="App-logo" />
            <p>
              欢迎来到美味食堂! 
              开始探索吧，享受美味，尽在指尖！
            </p>
          </div>
        ) : (
          <div>
            {user ? (
              <>
                <button onClick={handleLogout}>Logout</button>
                <Routes>
                  <Route path="/" element={<MainPage />} />
                  <Route path="/store/:storeId" element={<StorePage />} />
                  <Route path="/store/:storeId/item/:itemId" element={<ItemPage />} />
                </Routes>
              </>
            ) : (
              <Routes>
                <Route path="/login" element={<LoginForm setUser={setUser} />} />
                <Route path="/register" element={<RegisterForm />} />
                <Route path="/" element={<MainPage />} />
              </Routes>
            )}
          </div>
        )}
      </div>
    </Router>
  );
}

export default App;
