// src/components/Checkout.js
import React from 'react';
import { useCart } from '../context/CartContext';
import { useNavigate } from 'react-router-dom';
import './Checkout.css';

const Checkout = () => {
  const { cartItems, clearCart } = useCart();
  const navigate = useNavigate();

  const handlePayment = () => {
    // Implement payment processing logic here
    alert('Payment processing not implemented yet.');
    // Redirect to confirmation page after payment
    navigate('/confirmation');
  };

  const handlePayAtStore = () => {
    // Implement pay at store logic here
    alert('Order will be paid at the store.');
    // Send order details to staff and mark as payment pending
    sendOrderDetailsToStaff('PAYMENT PENDING');
    // Redirect to confirmation page
    navigate('/confirmation');
  };

  const sendOrderDetailsToStaff = (paymentStatus) => {
    // Implement logic to send order details to staff
    const orderDetails = {
      items: cartItems,
      paymentStatus: paymentStatus,
    };
    // Simulate sending order details to staff
    console.log('Order details sent to staff:', orderDetails);
    // Clear cart after sending order details
    clearCart();
  };

  return (
    <div className="checkout-container">
      <header className="checkout-header">
        <h1>结算</h1>
      </header>
      <div className="checkout-items">
        {cartItems.map(item => (
          <div key={item.id} className="checkout-item">
            {item.image && <img src={`/images/${item.image}`} alt={item.name} className="checkout-item-image" />}
            <div className="checkout-item-details">
              <h3>{item.name}</h3>
              <p>价格: {item.price} 元</p>
              <p>数量: {item.quantity}</p>
            </div>
          </div>
        ))}
      </div>
      <div className="checkout-actions">
        <button onClick={handlePayment} className="checkout-button">li'ke'fu'quan</button>
        <button onClick={handlePayAtStore} className="checkout-button">Pay at Store</button>
      </div>
    </div>
  );
};

export default Checkout;
