import React from 'react';
import { useParams } from 'react-router-dom';
import storeData from './data/storeData'; // Adjust the path as necessary
import './ItemPage.css'; // Create and adjust the path as necessary

function ItemPage() {
  const { storeId, itemId } = useParams();
  const store = storeData.find(store => store.id === parseInt(storeId));
  const item = store ? store.items.find(item => item.id === parseInt(itemId)) : null;

  if (!store || !item) {
    return <div>Item not found</div>;
  }

  return (
    <div className="item-page">
      <img src={item.image} alt={item.name} className="item-image" />
      <h1>{item.name}</h1>
      <p>{item.description}</p>
      <p>{item.weight}</p>
      <p>{item.price}</p>
    </div>
  );
}

export default ItemPage;
