import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useCart } from '../context/CartContext'; // Import the useCart hook
import './FormStyles.css'; // Importing the same CSS as for booking

const Preorder = ({ user }) => {
  const [pickupTime, setPickupTime] = useState('');
  const [storeName, setStoreName] = useState('');
  const [ticketNumber, setTicketNumber] = useState('');
  const { cartItems, clearCart } = useCart(); // Use the useCart hook to get cart items and clear the cart
  const navigate = useNavigate();
  const [idNumber, setIdNumber] = useState(user ? user.idNumber : ''); // Preload idNumber from user if available

  useEffect(() => {
    if (cartItems.length > 0) {
      setStoreName(cartItems[0].storeName); // Assuming all items in the cart are from the same store
    }
  }, [cartItems]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const preorderDetails = {
      userId: user ? user.id : '',
      idNumber: idNumber,
      storeId: cartItems[0].storeId, // Assuming all items in the cart are from the same store
      items: cartItems,
      pickupTime: pickupTime,
    };

    try {
      const response = await axios.post(`/api/stores/${cartItems[0].storeId}/preorder`, preorderDetails);
      if (response.status === 200) {
        setTicketNumber(response.data.ticketNumber);
        alert('Pre-order placed successfully');
        clearCart(); // Clear the cart after successful pre-order
        navigate('/confirmation', { state: { ticketNumber: response.data.ticketNumber } }); // Redirect to a confirmation page
      }
    } catch (error) {
      console.error('Error placing preorder:', error.response ? error.response.data : error.message);
      alert('Failed to place pre-order');
    }
  };

  return (
    <div className="container">
      <h1 className="form-title">Preorder</h1>
      {ticketNumber && <div className="ticket-number">Your Ticket Number: {ticketNumber}</div>}
      <form onSubmit={handleSubmit}>
        <label>
          User ID Number:
          <input type="text" value={idNumber} readOnly />
        </label>
        <label>
          Store Name:
          <input type="text" value={storeName} readOnly />
        </label>
        <label>
          Pickup Time:
          <input type="time" value={pickupTime} onChange={(e) => setPickupTime(e.target.value)} required />
        </label>
        <button type="submit">Submit Preorder</button>
      </form>
      <div className="cart-items">
        {cartItems.map((item) => (
          <div key={item.id} className="cart-item">
            <p>{item.name}</p>
            <p>{item.quantity} x {item.price} å…ƒ</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Preorder;
