import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../api/axios'; // Ensure correct path

const StaffDashboard = () => {
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    const fetchNotifications = async () => {
      try {
        const response = await api.get('/notifications');
        setNotifications(response.data);
      } catch (error) {
        console.error('Error fetching notifications:', error);
      }
    };

    fetchNotifications();
  }, []);

  return (
    <div>
      <h1>Staff Dashboard</h1>
      <ul>
        <li><Link to="/add-store">Add Store</Link></li>
        <li><Link to="/update-store">Update Store</Link></li>
        <li><Link to="/delete-store">Delete Store</Link></li>
        <li><Link to="/add-item">Add Item</Link></li>
        <li><Link to="/update-item">Update Item</Link></li>
        <li><Link to="/delete-item">Delete Item</Link></li>
      </ul>
      <div className="notifications">
        <h2>Notifications</h2>
        {notifications.length === 0 ? (
          <p>No new notifications.</p>
        ) : (
          <ul>
            {notifications.map((notification, index) => (
              <li key={index}>
                {notification.type === 'preorder' ? (
                  <p>Preorder: {notification.details}</p>
                ) : (
                  <p>Booking: {notification.details}</p>
                )}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default StaffDashboard;
