import React, { useState } from 'react';
import api from '../api/axios'; // Ensure correct path

function Preorder() {
  const [items, setItems] = useState([{ id: '', quantity: '' }]);
  const [pickupTime, setPickupTime] = useState('');

  const handleChange = (index, event) => {
    const values = [...items];
    values[index][event.target.name] = event.target.value;
    setItems(values);
  };

  const handleAddItem = () => {
    setItems([...items, { id: '', quantity: '' }]);
  };

  const handleRemoveItem = (index) => {
    const values = [...items];
    values.splice(index, 1);
    setItems(values);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const userId = localStorage.getItem('userId'); // Assuming userId is stored in localStorage
      const storeId = 1; // Assuming a specific storeId or make it dynamic
      await api.post(`/api/stores/${storeId}/preorder`, {
        userId,
        items,
        pickupTime
      });
      alert('Preorder placed successfully!');
      await api.post('/api/notify', { type: 'preorder', details: `Preorder by user ${userId}` });
    } catch (error) {
      console.error('Error placing preorder:', error);
      alert('Failed to place preorder');
    }
  };

  return (
    <div>
      <h2>Preorder</h2>
      <form onSubmit={handleSubmit}>
        {items.map((item, index) => (
          <div key={index}>
            <input
              type="text"
              name="id"
              value={item.id}
              onChange={(event) => handleChange(index, event)}
              placeholder="Item ID"
              required
            />
            <input
              type="number"
              name="quantity"
              value={item.quantity}
              onChange={(event) => handleChange(index, event)}
              placeholder="Quantity"
              required
            />
            <button type="button" onClick={() => handleRemoveItem(index)}>Remove</button>
          </div>
        ))}
        <button type="button" onClick={handleAddItem}>Add Item</button>
        <input
          type="datetime-local"
          value={pickupTime}
          onChange={(e) => setPickupTime(e.target.value)}
          placeholder="Pickup Time"
          required
        />
        <button type="submit">提交预定</button>
      </form>
    </div>
  );
}

export default Preorder;
