// src/components/OrderHistory.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FormStyles.css';

const OrderHistory = ({ user }) => {
  const [orders, setOrders] = useState([]);
  const [feedback, setFeedback] = useState('');
  const [rating, setRating] = useState(5);

  useEffect(() => {
    const fetchOrderHistory = async () => {
      try {
        const response = await axios.get(`/api/users/${user.id}/orders`);
        setOrders(response.data);
      } catch (error) {
        console.error('Error fetching order history:', error);
      }
    };

    fetchOrderHistory();
  }, [user]);

  const handleSubmitFeedback = async (orderId, orderType) => {
    try {
      await axios.post('/api/feedback', { userId: user.id, orderId, orderType, rating, comments: feedback });
      alert('Feedback submitted successfully');
      setFeedback('');
      setRating(5);
    } catch (error) {
      console.error('Error submitting feedback:', error);
      alert('Failed to submit feedback');
    }
  };

  return (
    <div className="container">
      <h1 className="form-title">订单历史</h1>
      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        <ul>
          {orders.map(order => (
            <li key={order.id}>
              <p>Order Date: {new Date(order.createdAt).toLocaleString()}</p>
              <p>Items:</p>
              <ul>
                {order.items.map(item => (
                  <li key={item.id}>
                    {item.name} - {item.quantity} x {item.price} 元
                  </li>
                ))}
              </ul>
              <p>Total Amount: {order.totalAmount} 元</p>
              <div>
                <label>
                  Rating:
                  <select value={rating} onChange={(e) => setRating(e.target.value)}>
                    {[1, 2, 3, 4, 5].map(score => (
                      <option key={score} value={score}>{score}</option>
                    ))}
                  </select>
                </label>
                <label>
                  Comments:
                  <textarea value={feedback} onChange={(e) => setFeedback(e.target.value)} required />
                </label>
                <button onClick={() => handleSubmitFeedback(order.id, order.type)}>Submit Feedback</button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default OrderHistory;
