// src/components/RegisterForm.js
import React, { useState } from 'react';
import axios from 'axios';
import './RegisterForm.css';

const RegisterForm = () => {
    const [id, setId] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('student'); // default role

    const handleRegister = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:5000/api/register', { id, password, role });
            alert('Registration successful');
            // Redirect or clear form as needed
        } catch (error) {
            if (role === 'staff') {
                alert('Registration failed for staff. Please try again.');
            } else {
                alert('Registration failed for student. Please try again.');
            }
            console.error('Registration failed:', error);
        }
    };

    return (
        <form onSubmit={handleRegister}>
            <input
                type="text"
                placeholder="ID Number"
                value={id}
                onChange={(e) => setId(e.target.value)}
            />
            <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
            />
            <select value={role} onChange={(e) => setRole(e.target.value)}>
                <option value="student">Student</option>
                <option value="staff">Staff</option>
            </select>
            <button type="submit">Register</button>
        </form>
    );
};

export default RegisterForm;
