import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './RegisterForm.css'; // Ensure you have CSS for styling

function RegisterForm() {
  const [id, setId] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('student'); // Default role to student
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const backendUrl = process.env.REACT_APP_BACKEND_URL;

  const handleRegister = async (event) => {
    event.preventDefault();

    const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    if (!passwordRegex.test(password)) {
      setError('Password must be at least 8 characters long and include a mixture of uppercase, lowercase, numbers, and special characters');
      return;
    }

    try {
      const response = await axios.post('/api/users/register', {
        id,
        username,
        password,
        role,
      });
      console.log('Register response:', response.data);
      navigate('/login');
    } catch (error) {
      console.error('Register error:', error.response ? error.response.data : error.message);
      setError(error.response ? error.response.data.message : 'Server error');
    }
  };

  return (
    <div className="register-form">
      <h2>注册</h2>
      <form onSubmit={handleRegister}>
        <div>
          <label>ID:</label>
          <input
            type="text"
            value={id}
            onChange={(e) => setId(e.target.value)}
            required
          />
        </div>
        <div>
          <label>密码:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <label>身份:</label>
          <select value={role} onChange={(e) => setRole(e.target.value)} required>
            <option value="student">学生</option>
            <option value="staff">人工</option>
          </select>
        </div>
        {error && <p className="error">{error}</p>}
        <button type="submit">注册</button>
      </form>
    </div>
  );
}

export default RegisterForm;
