import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../api/axios'; // Ensure correct path
import './storeStyles/store1.css';
import './storeStyles/store2.css';
import './storeStyles/store3.css';
import './storeStyles/store4.css';
import './storeStyles/store5.css';

function StorePage() {
  const { storeId } = useParams();
  const [store, setStore] = useState(null);
  const [items, setItems] = useState([]);
  const [quantities, setQuantities] = useState({});

  useEffect(() => {
    const fetchStoreData = async () => {
      try {
        const storeResponse = await api.get(`/stores/${storeId}`);
        const itemsResponse = await api.get(`/stores/${storeId}/items`);
        
        const storeData = storeResponse.data;
        const itemsData = itemsResponse.data;

        const initialQuantities = itemsData.reduce((acc, item) => {
          acc[item.id] = item.quantity || 1; // Initialize quantities
          return acc;
        }, {});

        setStore(storeData);
        setItems(itemsData);
        setQuantities(initialQuantities);
      } catch (error) {
        console.error('Error fetching store data:', error);
      }
    };

    fetchStoreData();
  }, [storeId]);

  const incrementQuantity = (itemId) => {
    setQuantities({
      ...quantities,
      [itemId]: quantities[itemId] + 1,
    });
  };

  const decrementQuantity = (itemId) => {
    setQuantities({
      ...quantities,
      [itemId]: Math.max(1, quantities[itemId] - 1), // Ensure quantity doesn't go below 1
    });
  };

  if (!store) {
    return <div>Loading store...</div>;
  }

  return (
    <div className={`store-page store-${storeId}`}>
      <div className="store-header">
      <img src={`/images/${store.icon}`} alt={store.name} className="store-icon" />
        <h1>{store.name}</h1>
        <img src={`/images/${store.icon}`} alt={store.name} className="store-icon" />
      </div>
      {storeId === "2" ? (
        <>
          <div className="soup-base">
            {items.slice(0, 2).map((item) => (
              <div key={item.id} className="store-item">
                <img src={item.image} alt={item.name} className="store-item-image" />
                <h3 className="store-item-name">{item.name}</h3>
                <p className="store-item-description">{item.description}</p>
                <p className="store-item-price">{item.price}</p>
                <div className="quantity-controls">
                  <button onClick={() => decrementQuantity(item.id)}>-</button>
                  <span>{quantities[item.id]}</span>
                  <button onClick={() => incrementQuantity(item.id)}>+</button>
                </div>
                <button className="add-to-cart-button">加入购物车</button>
              </div>
            ))}
          </div>
          <ul className="item-list">
            {items.slice(2).map((item) => (
              <li key={item.id} className="list-item">
                <span className="store-item-name">{item.name}</span>
                <span className="store-item-price">{item.weight} g {item.price} 元</span>
                <div className="quantity-controls">
                  <button onClick={() => decrementQuantity(item.id)}>-</button>
                  <span>{quantities[item.id]}</span>
                  <button onClick={() => incrementQuantity(item.id)}>+</button>
                </div>
                <button className="add-to-cart-button">加入购物车</button>
              </li>
            ))}
          </ul>
        </>
      ) : (
        <div className="store-items">
          {items.map((item) => (
            <div key={item.id} className="store-item">
              <Link to={`/store/${storeId}/item/${item.id}`} className="store-item-link">
                <img src={item.image} alt={item.name} className="store-item-image" />
              </Link>
              <h3 className="store-item-name">{item.name}</h3>
              <p className="store-item-description">{item.description}</p>
              <p className="store-item-weight">{item.weight}</p>
              <p className="store-item-price">{item.price} 元</p>
              <div className="quantity-controls">
                <button onClick={() => decrementQuantity(item.id)}>-</button>
                <span>{quantities[item.id]}</span>
                <button onClick={() => incrementQuantity(item.id)}>+</button>
              </div>
              <button className="add-to-cart-button">加入购物车</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default StorePage;
