import React from 'react';
import { useCart } from '../context/CartContext';
import { Link } from 'react-router-dom';
import './Cart.css';

const Cart = () => {
  const { cartItems, removeFromCart } = useCart();

  const getTotalCost = () => {
    return cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  if (!cartItems) {
    return <div>Loading cart...</div>;
  }

  return (
    <div className="cart-page">
      <header className="cart-header">
        <h1>Cart</h1>
        <div className="cart-actions">
          <Link to="/preorder" className="cart-action-button">Preorder</Link>
          <Link to="/checkout" className="cart-action-button">Checkout</Link>
        </div>
      </header>
      <div className="cart-container">
        {cartItems.length === 0 ? (
          <p>No items in cart.</p>
        ) : (
          <>
            <div className="cart-items">
              {cartItems.map(item => (
                <div key={item.id} className="cart-item">
                  {item.image && <img src={`/images/${item.image}`} alt={item.name} className="cart-item-image" />}
                  <div className="cart-item-details">
                    <h3>{item.name}</h3>
                    <p>Price: {item.price} 元</p>
                    <p>Quantity: {item.quantity}</p>
                    <button onClick={() => removeFromCart(item.id)} className="remove-from-cart-button">Remove</button>
                  </div>
                </div>
              ))}
            </div>
            <div className="cart-total">
              <h3>Total Cost: {getTotalCost()} 元</h3>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Cart;
