import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import storeData from './data/storeData'; // Adjust the path as necessary
import './StorePage.css'; // Adjust the path as necessary
import './FruitStore.css';
import './SpicySoupStore.css';
import './BurgerStore.css'

function StorePage() {
  const { storeId } = useParams();
  const store = storeData.find(store => store.id === parseInt(storeId));

  const [quantities, setQuantities] = useState(
    store.items.reduce((acc, item) => {
      acc[item.id] = item.quantity || 1; // Initialize quantities
      return acc;
    }, {})
  );

  const incrementQuantity = (itemId) => {
    setQuantities({
      ...quantities,
      [itemId]: quantities[itemId] + 1,
    });
  };

  const decrementQuantity = (itemId) => {
    setQuantities({
      ...quantities,
      [itemId]: Math.max(1, quantities[itemId] - 1), // Ensure quantity doesn't go below 1
    });
  };

  if (!store) {
    return <div>Store not found</div>;
  }

  return (
    <div className="store-page">
      <h1>{store.name}</h1>
      <div className="store-items">
        {store.items.map((item) => (
          <div key={item.id} className="store-item">
            <img src={item.image} alt={item.name} className="store-item-image" />
            <h3 className="store-item-name">{item.name}</h3>
            <p className="store-item-description">{item.description}</p>
            <p className="store-item-weight">{item.weight}</p>
            <p className="store-item-price">{item.price}</p>
            <div className="quantity-controls">
              <button onClick={() => decrementQuantity(item.id)}>-</button>
              <span>{quantities[item.id]}</span>
              <button onClick={() => incrementQuantity(item.id)}>+</button>
            </div>
            <button className="add-to-cart-button">加入购物车</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default StorePage;
