// src/components/DeleteStore.js
import React, { useState, useEffect } from 'react';
import axiosInstance from '../api/axios';

const DeleteStore = () => {
    const [stores, setStores] = useState([]);
    const [selectedStoreId, setSelectedStoreId] = useState('');

    useEffect(() => {
        const fetchStores = async () => {
            try {
                const response = await axiosInstance.get('/stores');
                setStores(response.data);
            } catch (error) {
                console.error('Error fetching stores:', error);
            }
        };
        fetchStores();
    }, []);

    const handleDeleteStore = async (e) => {
        e.preventDefault();
        try {
            await axiosInstance.delete(`/stores/${selectedStoreId}`);
            alert('Store deleted successfully');
            setStores(stores.filter(store => store.id !== selectedStoreId));
        } catch (error) {
            console.error('Error deleting store:', error);
            alert('Failed to delete store');
        }
    };

    return (
        <form onSubmit={handleDeleteStore}>
            <select
                value={selectedStoreId}
                onChange={(e) => setSelectedStoreId(e.target.value)}
            >
                <option value="">Select Store</option>
                {stores.map(store => (
                    <option key={store.id} value={store.id}>{store.name}</option>
                ))}
            </select>
            <button type="submit">Delete Store</button>
        </form>
    );
};

export default DeleteStore;
