import React, { useState } from 'react';
import api from '../api/axios'; // Ensure correct path

function Booking() {
  const [eventDetails, setEventDetails] = useState('');
  const [bookingTime, setBookingTime] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const userId = localStorage.getItem('userId'); // Assuming userId is stored in localStorage
      const storeId = 1; // Assuming a specific storeId or make it dynamic
      const response = await api.post('/api/booking', {
        userId,
        storeId,
        eventDetails,
        bookingTime
      });
      alert('Booking placed successfully!');
      await api.post('/api/notify', { type: 'booking', details: `Booking by user ${userId}` });
    } catch (error) {
      console.error('Error placing booking:', error);
      alert('Failed to place booking');
    }
  };

  return (
    <div>
      <h2>包厢预订</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          value={eventDetails}
          onChange={(e) => setEventDetails(e.target.value)}
          placeholder="Enter event details"
          required
        />
        <input
          type="datetime-local"
          value={bookingTime}
          onChange={(e) => setBookingTime(e.target.value)}
          placeholder="Booking Time"
          required
        />
        <button type="submit">提交yu'ding</button>
      </form>
    </div>
  );
}

export default Booking;
