import React, { useContext } from 'react';
import { useParams } from 'react-router-dom';
//import './Cart.css';

function Cart({ cart }) {
  const { storeId } = useParams();

  if (cart.length === 0) {
    return <div>Your cart is empty.</div>;
  }

  return (
    <div className="cart">
      <h1>Cart</h1>
      <ul>
        {cart.map(item => (
          <li key={item.id}>
            <h3>{item.name}</h3>
            <p>Quantity: {item.quantity}</p>
            <p>Price: {item.price} å…ƒ</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Cart;
